    select.id = "dynamicSelect";
    select.innerHTML = '<option value="" disabled selected>ржЬржмрж╛ржм ржмрж╛ржЫрзБржи</option>';
    for (const [value, text] of Object.entries(options)) {
      select.innerHTML += `<option value="${value}">${text}</option>`;
    }
    userInput.innerHTML = "";
